// MAIN SEED FUNCTION
// ===========================================
async function seedCity(city: CityData, places: PlaceData[]) {
  console.log(`\n🏙️  Seeding ${city.name}...`);
  
  // Check if city exists
  let dbCity = await prisma.city.findFirst({
    where: { slug: city.slug }
  });
  
  if (!dbCity) {
    // Create the city
    dbCity = await prisma.city.create({
      data: {
        id: city.slug,
        name: city.name,
        slug: city.slug,
        country: city.country,
        lat: city.coordinates[0],
        lng: city.coordinates[1],
        image: city.image,
        active: true,
      }
    });
    console.log(`  ✅ Created city: ${city.name}`);
  } else {
    console.log(`  ℹ️  City ${city.name} already exists`);
  }
  
  // Add places
  let created = 0;
  let skipped = 0;
  
  for (const place of places) {
    const placeSlug = slugify(place.name);
    // Include city slug to make globally unique
    const slug = `${city.slug}-${placeSlug}`;
    
    // Check if place exists (by name in this city)
    const existing = await prisma.place.findFirst({
      where: { 
        OR: [
          { slug },
          { name: place.name, cityId: dbCity.id }
        ]
      }
    });
    
    if (existing) {
      skipped++;
      continue;
    }
    
    // Map dogSizeAllowed to correct enum value
    const dogSizeAllowed = place.dogSizeAllowed 
      ? (DOG_SIZE_MAP[place.dogSizeAllowed] || 'all') 
      : undefined;
    
    await prisma.place.create({
      data: {
        id: generateId(),
        slug,
        name: place.name,
        type: place.type,
        cityId: dbCity.id,
        country: city.country,
        region: place.region || city.name,
        lat: place.lat,
        lng: place.lng,
        shortDescription: place.shortDescription,
        fullDescription: place.fullDescription,
        websiteUrl: place.websiteUrl,
        phone: place.phone,
        imageUrl: getRandomPhoto(place.type),
        dogSizeAllowed: dogSizeAllowed,
        hasWaterBowl: place.hasWaterBowl,
        offLeashAllowed: place.offLeashAllowed,
        hasOutdoorSeating: place.hasOutdoorSeating,
        petFee: place.petFee,
        maxDogsAllowed: place.maxDogsAllowed,
        rating: place.rating,
      }
    });
    created++;
  }
  
  console.log(`  📍 Created ${created} places, skipped ${skipped} existing`);
}

